import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import TaskCard from "../components/TaskCard";
import "./TaskView.css"


const TaskView = () => {
  const [tasks, setTasks] = useState([]);
  const [showConfirm, setShowConfirm] = useState(false);
  const [taskToDelete, setTaskToDelete] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Placeholder: Replace with API call in the future
    const fetchedTasks = [
      {
        title: "Complete project documentation",
        task_description: "Write all API documentation for the backend services",
        assignee: "John Doe",
        start_date: "2023-10-15",
        estimation: "8",
        due_date: "2023-11-15",
        status: "In Progress",
        priority: "High",
        type: "Task"
      },
      {
        title: "Implement user authentication",
        task_description: "Develop JWT-based authentication for the API",
        assignee: "Alice Smith",
        start_date: "2023-10-10",
        estimation: "12",
        due_date: "2023-10-25",
        status: "Completed",
        priority: "Critical",
        type: "Development"
      },
      {
        title: "Database schema optimization",
        task_description: "Review and optimize SQL queries and indexes",
        assignee: "Bob Johnson",
        start_date: "2023-10-20",
        estimation: "5",
        due_date: "2023-11-05",
        status: "Not Started",
        priority: "Medium",
        type: "Database"
      },
      {
        title: "Frontend dashboard redesign",
        task_description: "Update UI components for the admin dashboard",
        assignee: "Emily Davis",
        start_date: "2023-11-01",
        estimation: "10",
        due_date: "2023-11-20",
        status: "In Progress",
        priority: "High",
        type: "UI/UX"
      },
      {
        title: "Automated testing setup",
        task_description: "Configure Jest and Cypress for unit and E2E testing",
        assignee: "Michael Brown",
        start_date: "2023-10-05",
        estimation: "6",
        due_date: "2023-10-30",
        status: "Completed",
        priority: "Medium",
        type: "Testing"
      }
    ];
    setTasks(fetchedTasks);
  }, []);

  const handleAddTask = () => {
    navigate("/add-task");
  };

  const handleDeleteClick = (task) => {
    setTaskToDelete(task);
    setShowConfirm(true);
  };

  const handleConfirmDelete = () => {
    setTasks(tasks.filter(t => t !== taskToDelete));
    setShowConfirm(false);
    setTaskToDelete(null);
  };

  const handleCancelDelete = () => {
    setShowConfirm(false);
    setTaskToDelete(null);
  };

  return (
    <div className="task-view">

      <div className="card-container">
        {tasks.map((task, idx) => (
          <TaskCard
            key={idx}
            task={task}
            onDelete={() => handleDeleteClick(task)}
          />
        ))}
      </div>
      <div className="add-button-container">
        <button className="button" onClick={handleAddTask}>Add Task</button>
      </div>
      {showConfirm && (
        <div className="custom-confirm-overlay">
          <div className="custom-confirm-box">
            <p>Are you sure you want to delete this task?</p>
            <div className="confirm-buttons">
              <button className="button" onClick={handleConfirmDelete}>OK</button>
              <button className="button" onClick={handleCancelDelete}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>



  );



};
export default TaskView;
