import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./HierarchicalTaskList.css";

// Helper function to convert originalEstimate to days display
const formatEstimateAsDays = (originalEstimate) => {
    if (!originalEstimate) return 'N/A';

    // If it's a string, check for different formats
    if (typeof originalEstimate === 'string') {
        // Handle P{days}D format
        if (originalEstimate.startsWith('P') && originalEstimate.endsWith('D')) {
            const days = originalEstimate.substring(1, originalEstimate.length - 1);
            return `${days} days`;
        }
        // Handle PT{hours}H format (convert to days)
        if (originalEstimate.startsWith('PT') && originalEstimate.endsWith('H')) {
            const hours = parseFloat(originalEstimate.substring(2, originalEstimate.length - 1));
            const days = (hours / 8).toFixed(1); // Convert hours to days (8 hours per day)
            return `${days} days`;
        }
        return originalEstimate;
    }

    // If it's an object with duration properties, try to extract days
    if (typeof originalEstimate === 'object') {
        // Handle Java Duration object structure
        if (originalEstimate.seconds !== undefined) {
            const hours = originalEstimate.seconds / 3600; // Convert seconds to hours
            const days = (hours / 8).toFixed(1); // Convert hours to days
            return `${days} days`;
        }
        return JSON.stringify(originalEstimate);
    }

    return originalEstimate.toString();
};

// Individual task row component
const TaskRow = ({ task, level = 0, onDelete, isExpanded, onToggleExpand, hasChildren }) => {
    const navigate = useNavigate();

    const handleEdit = () => {
        navigate("/edit-task", { state: { task } });
    };

    const getIndentStyle = () => ({
        paddingLeft: `${level * 20 + 10}px`
    });

    const getTypeIcon = (type) => {
        switch(type?.toLowerCase()) {
            case 'epic': return 'üìä';
            case 'story': return 'üìù';
            case 'task': return '‚úì';
            case 'sub_task': return 'üî∏';
            default: return '‚Ä¢';
        }
    };

    return (
        <tr className={`task-row level-${level} ${task.type?.toLowerCase()}`}>
            <td style={getIndentStyle()}>
                <div className="task-title-container">
                    {hasChildren && (
                        <button 
                            className={`expand-collapse-btn ${isExpanded ? 'expanded' : ''}`}
                            onClick={onToggleExpand}
                            type="button"
                        >
                            {isExpanded ? '‚ñº' : '‚ñ∂'}
                        </button>
                    )}
                    {!hasChildren && <span className="no-children-spacer"></span>}
                    <span className="type-icon">{getTypeIcon(task.type)}</span>
                    <span className="task-title">{task.title}</span>
                </div>
            </td>
            <td>{task.description}</td>
            <td>{task.assigneeId || 'Unassigned'}</td>
            <td>
                <span className={`type-badge ${task.type?.toLowerCase()}`}>
                    {task.type}
                </span>
            </td>
            <td>
                <span className={`status-badge ${task.status?.toLowerCase()}`}>
                    {task.status}
                </span>
            </td>
            <td>
                <span className={`priority-badge ${task.priority?.toLowerCase()}`}>
                    {task.priority}
                </span>
            </td>
            <td>{formatEstimateAsDays(task.originalEstimate)}</td>
            <td>{task.startDate}</td>
            <td>{task.dueDate || 'N/A'}</td>
            <td>{Array.isArray(task.labels) ? task.labels.join(', ') : (task.labels || 'N/A')}</td>
            <td>
                <div className="button-container">
                    <button onClick={handleEdit} className="button-list edit">Edit</button>
                    <button onClick={() => onDelete(task)} className="button-list delete">Delete</button>
                </div>
            </td>
        </tr>
    );
};

// Recursive task list component
const HierarchicalTaskList = ({ tasks, onDelete, filter = "" }) => {
    const [expandedTasks, setExpandedTasks] = useState(new Set());

    const toggleExpand = (taskId) => {
        const newExpanded = new Set(expandedTasks);
        if (newExpanded.has(taskId)) {
            newExpanded.delete(taskId);
        } else {
            newExpanded.add(taskId);
        }
        setExpandedTasks(newExpanded);
    };

    const shouldShowTask = (task) => {
        if (!filter) return true;
        const searchTerm = filter.toLowerCase();
        return (
            task.title?.toLowerCase().includes(searchTerm) ||
            task.assigneeId?.toLowerCase().includes(searchTerm) ||
            task.status?.toLowerCase().includes(searchTerm) ||
            task.description?.toLowerCase().includes(searchTerm)
        );
    };

    const renderTasksRecursively = (taskList, level = 0) => {
        if (!taskList || taskList.length === 0) return null;

        return taskList.map((task) => {
            const taskId = task.taskId || task.id || task.task_id;
            const hasChildren = task.childTasks && task.childTasks.length > 0;
            const isExpanded = expandedTasks.has(taskId);
            const showThisTask = shouldShowTask(task);

            if (!showThisTask && (!hasChildren || level > 0)) {
                return null;
            }

            return (
                <React.Fragment key={taskId}>
                    {showThisTask && (
                        <TaskRow
                            task={task}
                            level={level}
                            onDelete={onDelete}
                            isExpanded={isExpanded}
                            onToggleExpand={() => toggleExpand(taskId)}
                            hasChildren={hasChildren}
                        />
                    )}
                    {hasChildren && (isExpanded || !showThisTask) && 
                        renderTasksRecursively(task.childTasks, level + 1)
                    }
                </React.Fragment>
            );
        });
    };

    if (!tasks || tasks.length === 0) {
        return (
            <tbody>
                <tr>
                    <td colSpan="11" className="no-tasks-message">
                        No tasks found
                    </td>
                </tr>
            </tbody>
        );
    }

    return (
        <tbody>
            {renderTasksRecursively(tasks)}
        </tbody>
    );
};

export default HierarchicalTaskList;
