// Import necessary hooks from React Router for navigation and location state
import { useLocation, useNavigate } from "react-router-dom";
// Import the useForm hook from react-hook-form for form management
import { useForm } from "react-hook-form";
// Import core React hooks for state and lifecycle management
import { useEffect, useState } from "react";
// Import the task API helper functions from your api/TaskApi.js file
import { getAllTasks, createTask, updateTask } from "../api/taskApi";
// Import the CSS file for styling the form
import "./TaskForm.css";

const TaskForm = () => {
    // Initialize navigation hook to programmatically redirect after submit
    const navigate = useNavigate();
    // Get current route/location, used for receiving task state (for edit)
    const location = useLocation();
    // Retrieve the task to edit from navigation state, if present
    const task = location.state?.task;

    // State to store the list of all tasks for the Parent Task dropdown
    const [allTasks, setAllTasks] = useState([]);

    // Fetch all tasks from the backend when the component first mounts
    useEffect(() => {
        // Define an async function to fetch tasks
        const fetchTasks = async () => {
            try {
                // Call the API helper to get all tasks
                const tasks = await getAllTasks();
                // Save tasks to component state
                setAllTasks(tasks);
            } catch (error) {
                // Log any errors to the console
                console.error("Error fetching tasks:", error);
            }
        };
        // Invoke the fetch function
        fetchTasks();
    }, []);

    // Initialize react-hook-form helpers
    const {
        register, // Register form fields
        handleSubmit, // Handler for form submission
        reset, // Function to reset form values
        formState: { errors }, // Object containing form validation errors
    } = useForm();

    // Prefill form fields for edit mode when a task is available
    useEffect(() => {
        if (task) {
            // Reset the form fields with the task's data
            reset({
                type: task.type || "",
                title: task.title || "",
                description: task.description || "",
                epicName: task.epicName || "",
                startDate: task.startDate || "",
                endDate: task.endDate || "",
                status: task.status || "",
                priority: task.priority || "",
                estimate: task.estimate || "",
                assignee: task.assignee || "",
                // Convert labels array to a comma-separated string, or pass an empty string
                labels: Array.isArray(task.labels) ? task.labels.join(", ") : (task.labels || ""),
                sprint: task.sprint || "",
                parentTask: task.parentTask || "",
            });
        }
    }, [task, reset]);

    // Handler for form submission
    const onSubmit = async (data) => {
        // Convert the labels input from comma-separated string to an array of trimmed strings
        const labelsArray = data.labels
            ? data.labels.split(",").map(l => l.trim()).filter(l => l.length > 0)
            : [];

        // Prepare the payload for the backend API with labels as an array
        const payload = {
            ...data,
            labels: labelsArray,
        };

        try {
            if (task && task.id) {
                // If editing, call updateTask with the correct task ID and updated data
                await updateTask(task.id, payload);
            } else {
                // If creating, call createTask with the payload
                await createTask(payload);
            }
            // After successful creation or update, navigate back to view tasks page
            navigate("/view-tasks");
        } catch (error) {
            // Log any submission errors
            console.error("Error saving task:", error);
        }
    };

    // Render the form UI
    return (
        <div className="form">
            <div className="form-header">
                {/* Display the form title depending on create or edit mode */}
                <h1>{task ? "Edit Task" : "+Create Task"}</h1>
            </div>
            <div className="form-container">
                {/* Use react-hook-form's handleSubmit for form validation and submission */}
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-subcontainer">
                        {/* Task Type Dropdown */}
                        <div className="type-name">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Type</label>
                            </div>
                            <select
                                {...register("type", { required: true })}
                                className="box"
                            >
                                <option value="">Select type</option>
                                <option value="task">Task</option>
                                <option value="epic">Epic</option>
                                <option value="story">Story</option>
                                <option value="sub_task">Sub Task</option>
                            </select>
                            {/* Show validation error if not selected */}
                            {errors.type && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>
                        {/* Task Title Input */}
                        <div className="type-name">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Task Name/Title</label>
                            </div>
                            <input
                                {...register("title", { required: true })}
                                className="box"
                            />
                            {/* Show validation error if not provided */}
                            {errors.title && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>
                    </div>
                    <div className="form-subcontainer">
                        {/* Task Description Textarea */}
                        <div className="description">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Description</label>
                            </div>
                            <textarea
                                {...register("description")}
                                rows={3}
                                className="box"
                            />
                        </div>
                    </div>
                    <div className="form-subcontainer">
                        {/* Epic Name Input */}
                        <div className="epic-name">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Linked Epic Name</label>
                            </div>
                            <input
                                {...register("epicName")}
                                className="box"
                            />
                        </div>
                    </div>
                    <div className="form-subcontainer">
                        {/* Start Date Input */}
                        <div className="date">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Start Date</label>
                            </div>
                            <input
                                type="date"
                                {...register("startDate")}
                                className="box"
                            />
                        </div>
                        {/* End Date Input */}
                        <div className="date">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">End Date/Due Date</label>
                            </div>
                            <input
                                type="date"
                                {...register("endDate")}
                                className="box"
                            />
                        </div>
                    </div>
                    <div className="form-subcontainer">
                        {/* Status Dropdown */}
                        <div className="status-priority">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Status</label>
                            </div>
                            <select
                                {...register("status", { required: true })}
                                className="box"
                            >
                                <option value="">Select status</option>
                                <option value="To Do">To Do</option>
                                <option value="In Progress">In Progress</option>
                                <option value="Done">Done</option>
                            </select>
                            {errors.status && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>
                        {/* Priority Dropdown */}
                        <div className="status-priority">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Priority</label>
                            </div>
                            <select
                                {...register("priority")}
                                className="box"
                            >
                                <option value="">Select priority</option>
                                <option value="High">High</option>
                                <option value="Medium">Medium</option>
                                <option value="Low">Low</option>
                            </select>
                        </div>
                    </div>
                    <div className="form-subcontainer">
                        {/* Original Estimate Input */}
                        <div className="time-assignee">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Original Estimate (hours)</label>
                            </div>
                            <input
                                type="number"
                                {...register("estimate")}
                                className="box"
                            />
                        </div>
                        {/* Assignee Input */}
                        <div className="time-assignee">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Assignee</label>
                            </div>
                            <input
                                {...register("assignee")}
                                className="box"
                            />
                        </div>
                    </div>
                    <div className="form-subcontainer">
                        {/* Labels Input */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Labels</label>
                            </div>
                            <input
                                {...register("labels")}
                                className="box"
                                placeholder="tag1, tag2, tag3"
                            />
                        </div>
                        {/* Sprint Input */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Sprint</label>
                            </div>
                            <input
                                {...register("sprint")}
                                className="box"
                            />
                        </div>
                        {/* Parent Task Dropdown */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Parent Task</label>
                            </div>
                            <select
                                {...register("parentTask")}
                                className="box"
                            >
                                <option value="">Select parent task</option>
                                {/* Populate options from allTasks */}
                                {allTasks.map((t) => (
                                    <option value={t.id} key={t.id}>
                                        {t.title}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </div>
                    <div className="form-subcontainer">
                        {/* Submit button, label changes depending on create or edit mode */}
                        <button
                            type="submit"
                            className="button"
                        >
                            {task ? "Update Task" : "Create Task"}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

// Export the TaskForm component so it can be imported elsewhere
export default TaskForm;
