import React, { useState } from 'react';
import './HierarchicalTaskRow.css';

const HierarchicalTaskRow = ({ 
    task, 
    level = 0, 
    onEdit, 
    onDelete, 
    onCreateSubtask,
    isExpanded,
    onToggleExpand,
    hasChildren 
}) => {
    const getTaskTypeIcon = (type) => {
        switch (type?.toLowerCase()) {
            case 'epic':
                return '📋';
            case 'story':
                return '📖';
            case 'task':
                return '✓';
            case 'subtask':
                return '•';
            default:
                return '📄';
        }
    };

    const getTaskTypeColor = (type) => {
        switch (type?.toLowerCase()) {
            case 'epic':
                return '#8B5CF6'; // Purple
            case 'story':
                return '#3B82F6'; // Blue
            case 'task':
                return '#10B981'; // Green
            case 'subtask':
                return '#6B7280'; // Gray
            default:
                return '#6B7280';
        }
    };

    const handleToggleExpand = (e) => {
        e.stopPropagation();
        onToggleExpand(task.id || task.taskId);
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'Not set';
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', { 
            month: 'short', 
            day: 'numeric', 
            year: 'numeric' 
        });
    };

    const formatEstimate = (originalEstimate) => {
        if (!originalEstimate) return 'No estimate';

        // Handle Duration string format from backend (e.g., "PT36H")
        if (typeof originalEstimate === 'string') {
            if (originalEstimate.startsWith('PT') && originalEstimate.endsWith('H')) {
                const hours = parseInt(originalEstimate.slice(2, -1));
                const days = hours / 8; // Assuming 8-hour workdays
                return `${days} day${days !== 1 ? 's' : ''}`;
            }
            if (originalEstimate.startsWith('P') && originalEstimate.includes('D')) {
                const days = parseFloat(originalEstimate.match(/P(\d+(?:\.\d+)?)D/)[1]);
                return `${days} day${days !== 1 ? 's' : ''}`;
            }
        }

        return originalEstimate.toString();
    };

    const getStatusBadgeClass = (status) => {
        if (!status) return 'status-default';
        switch (status.toLowerCase()) {
            case 'completed':
            case 'done':
                return 'status-completed';
            case 'in progress':
            case 'in-progress':
                return 'status-in-progress';
            case 'pending':
            case 'to do':
            case 'todo':
                return 'status-pending';
            default:
                return 'status-default';
        }
    };

    const getPriorityBadgeClass = (priority) => {
        if (!priority) return 'priority-default';
        switch (priority.toLowerCase()) {
            case 'high':
            case 'urgent':
                return 'priority-high';
            case 'medium':
            case 'normal':
                return 'priority-medium';
            case 'low':
                return 'priority-low';
            default:
                return 'priority-default';
        }
    };

    return (
        <div className={`hierarchical-task-card level-${level}`}>
            {/* Card header with hierarchy controls */}
            <div className="task-card-header">
                {/* Indentation and expand/collapse */}
                <div 
                    className="task-indentation" 
                    style={{ marginLeft: `${level * 24}px` }}
                >
                    {hasChildren && (
                        <button 
                            className={`expand-toggle ${isExpanded ? 'expanded' : 'collapsed'}`}
                            onClick={handleToggleExpand}
                            aria-label={isExpanded ? 'Collapse' : 'Expand'}
                        >
                            {isExpanded ? '▼' : '▶'}
                        </button>
                    )}
                    {!hasChildren && <div className="expand-spacer"></div>}
                </div>

                {/* Task type icon */}
                <div 
                    className="task-type-icon"
                    style={{ color: getTaskTypeColor(task.type) }}
                >
                    {getTaskTypeIcon(task.type)}
                </div>

                {/* Task title and type */}
                <div className="task-header-info">
                    <h3 className="task-title">{task.title}</h3>
                    <span className="task-type-label">{task.type}</span>
                </div>

                {/* Action buttons */}
                <div className="task-actions">
                    <button 
                        className="action-btn create-subtask-btn"
                        onClick={(e) => {
                            e.stopPropagation();
                            onCreateSubtask(task.id || task.taskId);
                        }}
                        title="Create Subtask"
                    >
                        ➕
                    </button>
                    <button 
                        className="action-btn edit-btn"
                        onClick={(e) => {
                            e.stopPropagation();
                            onEdit(task);
                        }}
                        title="Edit Task"
                    >
                        ✏️
                    </button>
                    <button 
                        className="action-btn delete-btn"
                        onClick={(e) => {
                            e.stopPropagation();
                            onDelete(task.id || task.taskId);
                        }}
                        title="Delete Task"
                    >
                        🗑️
                    </button>
                </div>
            </div>

            {/* Card body with all task details */}
            <div className="task-card-body">
                {/* Description row */}
                <div className="task-detail-row">
                    <div className="detail-label">Description:</div>
                    <div className="detail-value description-value">
                        {task.description || 'No description'}
                    </div>
                </div>

                {/* Assignee and Type row */}
                <div className="task-detail-row-dual">
                    <div className="detail-group">
                        <div className="detail-label">Assignee:</div>
                        <div className="detail-value assignee-value">
                            {task.assigneeId || 'Unassigned'}
                        </div>
                    </div>
                    <div className="detail-group">
                        <div className="detail-label">Type:</div>
                        <div className="detail-value type-value">
                            {task.type}
                        </div>
                    </div>
                </div>

                {/* Status and Priority row */}
                <div className="task-detail-row-dual">
                    <div className="detail-group">
                        <div className="detail-label">Status:</div>
                        <div className="detail-value">
                            <span className={`status-badge ${getStatusBadgeClass(task.status)}`}>
                                {task.status || 'No Status'}
                            </span>
                        </div>
                    </div>
                    <div className="detail-group">
                        <div className="detail-label">Priority:</div>
                        <div className="detail-value">
                            <span className={`priority-badge ${getPriorityBadgeClass(task.priority)}`}>
                                {task.priority || 'Medium'}
                            </span>
                        </div>
                    </div>
                </div>

                {/* Estimation row */}
                <div className="task-detail-row">
                    <div className="detail-label">Estimation:</div>
                    <div className="detail-value estimation-value">
                        {formatEstimate(task.originalEstimate)}
                    </div>
                </div>

                {/* Start Date and Due Date row */}
                <div className="task-detail-row-dual">
                    <div className="detail-group">
                        <div className="detail-label">Start Date:</div>
                        <div className="detail-value date-value">
                            {formatDate(task.startDate)}
                        </div>
                    </div>
                    <div className="detail-group">
                        <div className="detail-label">Due Date:</div>
                        <div className="detail-value date-value">
                            {formatDate(task.dueDate)}
                        </div>
                    </div>
                </div>

                {/* Labels row */}
                <div className="task-detail-row">
                    <div className="detail-label">Labels:</div>
                    <div className="detail-value labels-value">
                        {Array.isArray(task.labels) ? task.labels.join(', ') : (task.labels || 'N/A')}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default HierarchicalTaskRow;
