// Import hooks and form helpers
import { useLocation, useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { useEffect, useState } from "react";
// Import API helpers for task operations
import { getAllTasks, createTask, updateTask } from "../api/taskApi";
// Import CSS for styling
import "./TaskForm.css";

const TaskForm = () => {
    // Get navigation function
    const navigate = useNavigate();
    // Get location to check if we are editing a task
    const location = useLocation();
    // Extract task from navigation state if present
    const task = location.state?.task;

    // State to hold all tasks for parent task dropdown
    const [allTasks, setAllTasks] = useState([]);

    // Set up react-hook-form for handling form data
    const {
        register,
        handleSubmit,
        reset,
        formState: { errors },
    } = useForm();

    // Fetch all tasks for parent dropdown when component loads
    useEffect(() => {
        // Call backend to get all tasks
        getAllTasks().then(setAllTasks);
    }, []);

    // If editing, prefill form with existing task data
    useEffect(() => {
        if (task) {
            reset({
                type: task.type || "",
                title: task.title || "",
                description: task.task_description || "",
                epicName: task.epicName || "",
                startDate: task.start_date || "",
                endDate: task.due_date || "",
                status: task.status || "",
                priority: task.priority || "",
                estimate: task.estimation || "",
                assignee: task.assignee || "",
                labels: task.labels || "",
                sprint: task.sprint || "",
                parentTask: task.parentTask || "",
            });
        }
    }, [task, reset]);

    // Handle form submission
    const onSubmit = async (data) => {
        if (task && task.id) {
            // If editing, call API to update the task
            await updateTask(task.id, { ...task, ...data });
        } else {
            // If creating, call API to create a new task
            await createTask(data);
        }
        // After saving, navigate back to the task list page
        navigate("/view-tasks");
    };

    return (
        <div className="form">
            <div className="form-header">
                {/* Show different title for editing vs creating */}
                <h1>{task ? "Edit Task" : "+Create Task"}</h1>
            </div>
            <div className="form-container">
                <form onSubmit={handleSubmit(onSubmit)}>
                    {/* Type field */}
                    <div className="form-subcontainer">
                        <div className="type-name">
                            <div className="title">
                                <label>Type</label>
                            </div>
                            <select {...register("type", { required: true })} className="box">
                                <option value="">Select type</option>
                                <option value="task">Task</option>
                                <option value="epic">Epic</option>
                                <option value="story">Story</option>
                                <option value="sub_task">Sub Task</option>
                            </select>
                            {errors.type && (
                                <span className="text-red-500 text-sm">This field is required</span>
                            )}
                        </div>
                    </div>
                    {/* Title field */}
                    <div className="form-subcontainer">
                        <div className="type-name">
                            <div className="title">
                                <label>Task Title</label>
                            </div>
                            <input
                                {...register("title", { required: true })}
                                className="box"
                            />
                            {errors.title && (
                                <span className="text-red-500 text-sm">This field is required</span>
                            )}
                        </div>
                    </div>
                    {/* Description field */}
                    <div className="form-subcontainer">
                        <div className="description">
                            <div className="title">
                                <label>Description</label>
                            </div>
                            <textarea {...register("description")} rows={3} className="box" />
                        </div>
                    </div>
                    {/* Epic Name field */}
                    <div className="form-subcontainer">
                        <div className="epic-name">
                            <div className="title">
                                <label>Linked Epic Name</label>
                            </div>
                            <input {...register("epicName")} className="box" />
                        </div>
                    </div>
                    {/* Start Date and End Date fields */}
                    <div className="form-subcontainer">
                        <div className="date">
                            <div className="title">
                                <label>Start Date</label>
                            </div>
                            <input type="date" {...register("startDate")} className="box" />
                        </div>
                        <div className="date">
                            <div className="title">
                                <label>End Date/Due Date</label>
                            </div>
                            <input type="date" {...register("endDate")} className="box" />
                        </div>
                    </div>
                    {/* Status and Priority fields */}
                    <div className="form-subcontainer">
                        <div className="status-priority">
                            <div className="title">
                                <label>Status</label>
                            </div>
                            <select {...register("status", { required: true })} className="box">
                                <option value="">Select status</option>
                                <option value="To Do">To Do</option>
                                <option value="In Progress">In Progress</option>
                                <option value="Done">Done</option>
                            </select>
                            {errors.status && (
                                <span className="text-red-500 text-sm">This field is required</span>
                            )}
                        </div>
                        <div className="status-priority">
                            <div className="title">
                                <label>Priority</label>
                            </div>
                            <select {...register("priority")} className="box">
                                <option value="">Select priority</option>
                                <option value="High">High</option>
                                <option value="Medium">Medium</option>
                                <option value="Low">Low</option>
                            </select>
                        </div>
                    </div>
                    {/* Estimate and Assignee fields */}
                    <div className="form-subcontainer">
                        <div className="time-assignee">
                            <div className="title">
                                <label>Original Estimate (hours)</label>
                            </div>
                            <input type="number" {...register("estimate")} className="box" />
                        </div>
                        <div className="time-assignee">
                            <div className="title">
                                <label>Assignee</label>
                            </div>
                            <input {...register("assignee")} className="box" />
                        </div>
                    </div>
                    {/* Labels, Sprint, Parent Task fields */}
                    <div className="form-subcontainer">
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label>Labels</label>
                            </div>
                            <input
                                {...register("labels")}
                                className="box"
                                placeholder="tag1, tag2, tag3"
                            />
                        </div>
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label>Sprint</label>
                            </div>
                            <input {...register("sprint")} className="box" />
                        </div>
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label>Parent Task</label>
                            </div>
                            <select {...register("parentTask")} className="box">
                                <option value="">Select parent task</option>
                                {/* Render all tasks for parent task selection */}
                                {allTasks.map((t) => (
                                    <option key={t.id} value={t.title}>
                                        {t.title}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </div>
                    {/* Submit button */}
                    <div className="form-subcontainer">
                        <button type="submit" className="button">
                            {task ? "Update Task" : "Create Task"}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default TaskForm;
