import { useLocation, useNavigate } from "react-router-dom"; // Import hooks for route navigation and location
import { useForm } from "react-hook-form"; // Import the react-hook-form library for form handling
import { useEffect, useState } from "react"; // Import React hooks for managing component state and effects
import { createTaskApi, updateTaskApi } from "../api/TaskApi"; // Import API functions for creating and updating tasks
import "./TaskForm.css"; // Import the CSS file for styling

const TaskForm = () => {
    const navigate = useNavigate(); // Hook to programmatically navigate between routes
    const location = useLocation(); // Hook to get the current route/location
    const task = location.state?.task; // Get the task to edit if passed through navigation state (for edit mode)

    const [allTasks, setAllTasks] = useState([]); // State to hold all tasks for the parent task dropdown

    // Fetch all tasks for the parent task dropdown when the component mounts
    useEffect(() => {
        const fetchTasks = async () => {
            try {
                // Call your backend API to get all tasks (replace with your API endpoint as needed)
                const response = await fetch("/api/tasks"); // Fetch tasks from backend
                if (response.ok) {
                    const tasks = await response.json(); // Parse JSON response
                    setAllTasks(tasks); // Set tasks in component state
                }
            } catch (error) {
                // Handle fetch error
                console.error("Error fetching tasks:", error);
            }
        };
        fetchTasks(); // Call the function on component mount
    }, []);

    // Set up react-hook-form
    const {
        register, // Register form fields for validation and value tracking
        handleSubmit, // Handle form submission event
        reset, // Function to reset form fields
        formState: { errors }, // Form validation errors
    } = useForm();

    // Prefill the form fields if editing an existing task
    useEffect(() => {
        if (task) {
            reset({
                type: task.type ? task.type.toLowerCase() : "", // Set type, default to lowercase if exists
                title: task.title || "", // Set title if exists
                description: task.description || "", // Set description if exists
                epicName: task.epicName || "", // Set epic name if exists
                startDate: task.start_date || "", // Set start date if exists
                endDate: task.due_date || "", // Set due date if exists
                status: task.status || "", // Set status if exists
                priority: task.priority || "", // Set priority if exists
                estimate: task.original_estimate || "", // Set original estimate if exists
                assignee: task.assignee_id || "", // Set assignee if exists
                // Convert labels array to comma-separated string for input field
                labels: Array.isArray(task.labels) ? task.labels.join(", ") : (task.labels || ""),
                sprint: task.sprint_id || "", // Set sprint ID if exists
                parentTask: task.parent_task_id || "", // Set parent task ID if exists
            });
        }
    }, [task, reset]); // Runs whenever the task or reset function changes

    // Handle form submission
    const onSubmit = async (data) => {
        // Convert the comma-separated labels string to an array of trimmed, non-empty strings
        const labelsArray = data.labels
            ? data.labels.split(',').map(l => l.trim()).filter(l => l.length > 0)
            : [];

        // Build the payload for API with labels as an array
        const taskPayload = {
            ...data, // Spread the rest of the form data as-is
            labels: labelsArray, // Overwrite labels with the properly formatted array
        };

        try {
            if (task) {
                // If editing, call the update API
                await updateTaskApi(task.taskId, taskPayload); // Pass the task ID and payload
            } else {
                // If creating, call the create API
                await createTaskApi(taskPayload); // Pass the payload directly
            }
            navigate("/"); // After successful submission, navigate to the home page
        } catch (error) {
            // Handle errors during API call
            console.error("Error saving task:", error);
            // Optionally, display an error message to the user here
        }
    };

    return (
        <div className="form">{/* Main wrapper for the form */}
            <div className="form-header">{/* Header section */}
                <h1>{task ? "Edit Task" : "+Create Task"}</h1> {/* Header changes depending on mode */}
            </div>
            <div className="form-container">{/* Container for the form fields */}
                <form onSubmit={handleSubmit(onSubmit)}>{/* Form element with react-hook-form submit handler */}
                    <div className="form-subcontainer">{/* Subcontainer for type and title */}
                        <div className="type-name">
                            {/* Task type dropdown */}
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Type</label>
                            </div>
                            <select
                                {...register("type", { required: true })} // Register for validation
                                className="box"
                            >
                                <option value="">Select type</option>
                                <option value="task">Task</option>
                                <option value="epic">Epic</option>
                                <option value="story">Story</option>
                                <option value="sub_task">Sub Task</option>
                            </select>
                            {errors.type && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>
                        <div className="type-name">
                            {/* Task title input */}
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Task Name/Title</label>
                            </div>
                            <input
                                {...register("title", { required: true })} // Register for validation
                                className="box"
                            />
                            {errors.title && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>
                    </div>
                    <div className="form-subcontainer">{/* Subcontainer for description */}
                        {/* Task description textarea */}
                        <div className="description">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Description</label>
                            </div>
                            <textarea
                                {...register("description")}
                                rows={3}
                                className="box"
                            />
                        </div>
                    </div>
                    <div className="form-subcontainer">{/* Subcontainer for epic name */}
                        {/* Linked Epic Name input */}
                        <div className="epic-name">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Linked Epic Name</label>
                            </div>
                            <input
                                {...register("epicName")}
                                className="box"
                            />
                        </div>
                    </div>
                    <div className="form-subcontainer">{/* Subcontainer for dates */}
                        {/* Start Date input */}
                        <div className="date">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Start Date</label>
                            </div>
                            <input
                                type="date"
                                {...register("startDate")}
                                className="box"
                            />
                        </div>
                        {/* End Date/Due Date input */}
                        <div className="date">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">End Date/Due Date</label>
                            </div>
                            <input
                                type="date"
                                {...register("endDate")}
                                className="box"
                            />
                        </div>
                    </div>
                    <div className="form-subcontainer">{/* Subcontainer for status and priority */}
                        {/* Status dropdown */}
                        <div className="status-priority">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Status</label>
                            </div>
                            <select
                                {...register("status", { required: true })}
                                className="box"
                            >
                                <option value="">Select status</option>
                                <option value="To Do">To Do</option>
                                <option value="In Progress">In Progress</option>
                                <option value="Done">Done</option>
                            </select>
                            {errors.status && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>
                        {/* Priority dropdown */}
                        <div className="status-priority">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Priority</label>
                            </div>
                            <select
                                {...register("priority")}
                                className="box"
                            >
                                <option value="">Select priority</option>
                                <option value="High">High</option>
                                <option value="Medium">Medium</option>
                                <option value="Low">Low</option>
                            </select>
                        </div>
                    </div>
                    <div className="form-subcontainer">{/* Subcontainer for estimate and assignee */}
                        {/* Original Estimate input */}
                        <div className="time-assignee">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Original Estimate (hours)</label>
                            </div>
                            <input
                                type="number"
                                {...register("estimate")}
                                className="box"
                            />
                        </div>
                        {/* Assignee input */}
                        <div className="time-assignee">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Assignee</label>
                            </div>
                            <input
                                {...register("assignee")}
                                className="box"
                            />
                        </div>
                    </div>
                    <div className="form-subcontainer">{/* Subcontainer for labels, sprint, parent task */}
                        {/* Labels/Tags input */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Labels</label>
                            </div>
                            <input
                                {...register("labels")}
                                className="box"
                                placeholder="tag1, tag2, tag3"
                            />
                        </div>
                        {/* Sprint input */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Sprint</label>
                            </div>
                            <input
                                {...register("sprint")}
                                className="box"
                            />
                        </div>
                        {/* Parent Task dropdown */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Parent Task</label>
                            </div>
                            <select
                                {...register("parentTask")}
                                className="box"
                            >
                                <option value="">Select parent task</option>
                                {allTasks.map((t) => (
                                    <option value={t.taskId} key={t.taskId}>
                                        {t.title}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </div>
                    <div className="form-subcontainer">{/* Subcontainer for submit button */}
                        <button
                            type="submit"
                            className="button"
                        >
                            {task ? "Update Task" : "Create Task"}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default TaskForm; // Export the form component so it can be used elsewhere
