        labels: data.labels ? data.labels.split(',').map(l => l.trim()) : [],
        parentTaskId: data.parentTask // Make sure this is a UUID if required
    };

    try {
        if (task) {
            // Update existing task
            await fetch(`/tasks/${task.id || task.taskId}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(payload)
            });
        } else {
            // Create new task
            await fetch("/tasks", {
