import React, { useState, useEffect } from 'react';
import HierarchicalTaskRow from './HierarchicalTaskRow';
import './HierarchicalTaskTree.css';

const HierarchicalTaskTree = ({ 
    tasks = [], 
    onEdit, 
    onDelete, 
    onCreateSubtask,
    expandedTasks = new Set(),
    onToggleExpand 
}) => {
    const [loading, setLoading] = useState(false);

    // Check if tasks is hierarchy data or flat data
    const isHierarchyData = tasks.length > 0 && tasks[0].hasOwnProperty('children');

    // Convert hierarchy data to flat list for processing
    const flattenHierarchy = (hierarchyData) => {
        let flattened = [];

        const processNode = (node) => {
            // Add the main task
            if (node.taskId || node.id) {
                flattened.push({
                    id: node.taskId || node.id,
                    taskId: node.taskId || node.id,
                    title: node.title,
                    type: node.type,
                    status: node.status,
                    priority: node.priority,
                    startDate: node.startDate,
                    dueDate: node.dueDate,
                    originalEstimate: node.originalEstimate,
                    assigneeId: node.assigneeId,
                    labels: node.labels,
                    parentTaskId: node.parentTaskId,
                    description: node.description
                });
            }

            // Process children recursively
            if (node.children && Array.isArray(node.children)) {
                node.children.forEach(child => processNode(child));
            }
        };

        hierarchyData.forEach(node => processNode(node));
        return flattened;
    };

    // Build a map of parent-child relationships
    const buildTaskMap = (taskList) => {
        const childrenMap = new Map();
        const rootTasks = [];

        taskList.forEach(task => {
            if (task.parentTaskId) {
                if (!childrenMap.has(task.parentTaskId)) {
                    childrenMap.set(task.parentTaskId, []);
                }
                childrenMap.get(task.parentTaskId).push(task);
            } else {
                rootTasks.push(task);
            }
        });

        return { childrenMap, rootTasks };
    };

    // Get the task list to work with
    const workingTasks = isHierarchyData ? flattenHierarchy(tasks) : tasks;
    const { childrenMap, rootTasks } = buildTaskMap(workingTasks);

    // Check if a task has children
    const hasChildren = (taskId) => {
        return childrenMap.has(taskId) && childrenMap.get(taskId).length > 0;
    };

    // Get children of a task
    const getChildren = (taskId) => {
        return childrenMap.get(taskId) || [];
    };

    // Recursive function to render task tree
    const renderTaskTree = (taskList, level = 0) => {
        if (!taskList || taskList.length === 0) {
            return null;
        }

        return taskList.map(task => {
            const taskId = task.id || task.taskId;
            return (
                <div key={taskId} className="task-tree-node">
                    <HierarchicalTaskRow
                        task={task}
                        level={level}
                        onEdit={onEdit}
                        onDelete={onDelete}
                        onCreateSubtask={onCreateSubtask}
                        isExpanded={expandedTasks.has(taskId)}
                        onToggleExpand={onToggleExpand}
                        hasChildren={hasChildren(taskId)}
                    />

                    {/* Render children if expanded */}
                    {expandedTasks.has(taskId) && hasChildren(taskId) && (
                        <div className="task-children">
                            {renderTaskTree(getChildren(taskId), level + 1)}
                        </div>
                    )}
                </div>
            );
        });
    };

    // Handle expand all / collapse all
    const handleExpandAll = () => {
        const allTaskIds = new Set();
        const collectTaskIds = (taskList) => {
            taskList.forEach(task => {
                allTaskIds.add(task.id || task.taskId);
                if (hasChildren(task.id || task.taskId)) {
                    collectTaskIds(getChildren(task.id || task.taskId));
                }
            });
        };
        collectTaskIds(workingTasks);

        allTaskIds.forEach(taskId => {
            if (hasChildren(taskId)) {
                onToggleExpand(taskId, true);
            }
        });
    };

    const handleCollapseAll = () => {
        expandedTasks.forEach(taskId => {
            onToggleExpand(taskId, false);
        });
    };

    return (
        <div className="hierarchical-task-tree">
            {/* Tree controls */}
            <div className="tree-controls">
                <div className="tree-actions">
                    <button 
                        className="tree-control-btn expand-all-btn"
                        onClick={handleExpandAll}
                        disabled={loading}
                    >
                        Expand All
                    </button>
                    <button 
                        className="tree-control-btn collapse-all-btn"
                        onClick={handleCollapseAll}
                        disabled={loading}
                    >
                        Collapse All
                    </button>
                </div>

                <div className="tree-stats">
                    <span className="stats-text">
                        {rootTasks.length} root task{rootTasks.length !== 1 ? 's' : ''}, 
                        {workingTasks.length} total task{workingTasks.length !== 1 ? 's' : ''}
                    </span>
                </div>
            </div>

            {/* Tree header */}
            <div className="tree-header">
                <div className="header-section task-name-header">Task</div>
                <div className="header-section task-details-header">
                    <span>Status</span>
                    <span>Dates</span>
                    <span>Estimate</span>
                    <span>Assignee</span>
                </div>
                <div className="header-section task-actions-header">Actions</div>
            </div>

            {/* Task tree */}
            <div className="task-tree-container">
                {loading ? (
                    <div className="tree-loading">
                        <div className="loading-spinner"></div>
                        <span>Loading tasks...</span>
                    </div>
                ) : rootTasks.length === 0 ? (
                    <div className="tree-empty">
                        <div className="empty-icon">ðŸ“‹</div>
                        <h3>No tasks found</h3>
                        <p>Create your first task to get started</p>
                    </div>
                ) : (
                    renderTaskTree(rootTasks)
                )}
            </div>
        </div>
    );
};

export default HierarchicalTaskTree;
