import "./TaskCard.css"



const TaskCard=({task})=>{
    const handleDelete = () => {
        console.log("Task to be deleted:", task);
        // In the future, you'll add API call here to delete from backend
    };

    const handleEdit = () => {
        console.log("Task to be edited:", task);
        // In the future, you'll add API call here to edit the task
    }

    return(
        <div className="task-card">
            <div className="task-title">
                <h1>{task.title}</h1>
            </div>
            <div className="description">
                <span>
                    {task.task_description}
                </span>
            </div>
            <div className="assignee-estimation">
                <div className="assignee">
                    <div className="section-title">Assgne:</div>
                    <div className="content">{task.assignee}</div>

                </div>
                <div className="estimation">
                    <div className="title">Estimate:</div>
                    <div className="content">{task.estimation}h</div>

                </div>


            </div>
            <div className="date">
                <div className="start-date">
                    <div className="title">Start: </div>
                    <div className="content">{task.start_date}</div>

                </div>
                <div className="end-date">
                    <div className="title">Due: </div>
                    <div className="content">{task.due_date}</div>

                </div>

            </div>
            <div className="task-profile">
                <div className="state">{task.type}</div>
                <div className="state">{task.status}</div>
                <div className="state">{task.priority}</div>


            </div>
            <div className="line">

            </div>
            <div className="button-container">
                <button onClick={handleEdit} className="button">Edit</button>
                <button onClick={handleDelete}>Delete</button>
            </div>

        </div>

    );
};
export default TaskCard;
