import { useLocation, useNavigate, useSearchParams } from "react-router-dom";
import { useForm } from "react-hook-form";
import { useEffect, useState } from "react";
import useDateCalculation from "../hooks/useDateCalculation";
import HolidayCalendar from "../components/HolidayCalendar";
import "./TaskForm.css"

const TaskForm = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [searchParams] = useSearchParams();
    const task = location.state?.task; // get task if passed
    const parentId = searchParams.get('parentId'); // Get parent ID from URL

    const [allTasks, setAllTasks] = useState([]);
    const [validParentTasks, setValidParentTasks] = useState([]);
    const [selectedTaskType, setSelectedTaskType] = useState('task');
    const [parentTask, setParentTask] = useState(null);
    const [startDate, setStartDate] = useState('');
    const [estimateDays, setEstimateDays] = useState('');
    const [endDateValue, setEndDateValue] = useState('');

    // Use the date calculation hook
    const {
        holidays,
        calculatedEndDate,
        isCalculating,
        validationMessage,
        calculateEndDate,
        validateEndDate,
        getHolidayInfo,
        shouldDisableDate,
        getDateClassName
    } = useDateCalculation();

    // Fetch all tasks for parent task dropdown and handle parent ID from URL
    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await fetch("/tasks");
                if (!response.ok) throw new Error("Failed to fetch tasks");
                const tasks = await response.json();
                setAllTasks(tasks);

                // If parentId is in URL, find and set the parent task
                if (parentId) {
                    const parent = tasks.find(t => (t.id || t.taskId) === parentId);
                    if (parent) {
                        setParentTask(parent);
                        // Determine appropriate task type based on parent
                        const taskType = getChildTaskType(parent.type);
                        setSelectedTaskType(taskType);
                        setValue('type', taskType);
                        setValue('parentTask', parent.taskId || parent.id);
                    }
                }
            } catch (error) {
                console.error('Error fetching tasks:', error);
                setAllTasks([]);
            }
        };
        fetchTasks();
    }, [parentId, setValue]);

    // Fetch valid parent tasks when task type changes
    useEffect(() => {
        const fetchValidParents = async () => {
            if (!selectedTaskType) return;

            try {
                const response = await fetch(`/tasks/valid-parents/${selectedTaskType}`);
                if (!response.ok) throw new Error("Failed to fetch valid parent tasks");
                const validParents = await response.json();
                setValidParentTasks(validParents);
            } catch (error) {
                console.error('Error fetching valid parent tasks:', error);
                setValidParentTasks([]);
            }
        };
        fetchValidParents();
    }, [selectedTaskType]);

    // Helper function to determine child task type based on parent type
    const getChildTaskType = (parentType) => {
        switch (parentType?.toLowerCase()) {
            case 'epic':
                return 'story'; // Default to story for epic children
            case 'story':
            case 'task':
                return 'sub_task';
            default:
                return 'task';
        }
    };

    // Handle task type change
    const handleTaskTypeChange = (e) => {
        const newType = e.target.value;
        setSelectedTaskType(newType);
        setValue('type', newType);

        // Clear parent task if not compatible
        if (parentTask && !isValidHierarchy(newType, parentTask.type)) {
            setParentTask(null);
            setValue('parentTask', '');
        }
    };

    // Validate hierarchy relationship
    const isValidHierarchy = (taskType, parentType) => {
        if (!parentType) return taskType === 'epic'; // Only epics can have no parent

        switch (taskType?.toLowerCase()) {
            case 'epic':
                return false; // Epics cannot have parents
            case 'story':
            case 'task':
                return parentType?.toLowerCase() === 'epic';
            case 'sub_task':
                return ['story', 'task'].includes(parentType?.toLowerCase());
            default:
                return false;
        }
    };

    const {
        register,
        handleSubmit,
        reset,
        setValue,
        formState: { errors },
    } = useForm();

    // Helper function to extract days from originalEstimate
    const extractDaysFromEstimate = (originalEstimate) => {
        if (!originalEstimate) return "";

        // If it's a string, check for different formats
        if (typeof originalEstimate === 'string') {
            // Handle P{days}D format
            if (originalEstimate.startsWith('P') && originalEstimate.endsWith('D')) {
                return originalEstimate.substring(1, originalEstimate.length - 1);
            }
            // Handle PT{hours}H format (convert to days)
            if (originalEstimate.startsWith('PT') && originalEstimate.endsWith('H')) {
                const hours = parseFloat(originalEstimate.substring(2, originalEstimate.length - 1));
                return (hours / 8).toString(); // Convert hours to days (8 hours per day)
            }
        }

        // If it's an object with duration properties, try to extract days
        if (typeof originalEstimate === 'object') {
            // Handle Java Duration object structure
            if (originalEstimate.seconds !== undefined) {
                const hours = originalEstimate.seconds / 3600; // Convert seconds to hours
                return (hours / 8).toString(); // Convert hours to days
            }
        }

        return originalEstimate.toString();
    };

    // Prefill form if editing
    useEffect(() => {
        if (task) {
            const startDateVal = task.start_date || task.startDate || "";
            const endDateVal = task.due_date || task.dueDate || "";
            const estimateVal = extractDaysFromEstimate(task.originalEstimate);

            setStartDate(startDateVal);
            setEndDateValue(endDateVal);
            setEstimateDays(estimateVal);
            setSelectedTaskType(task.type || 'task');

            reset({
                type: task.type || "",
                title: task.title || "",
                description: task.task_description || task.description || "",
                epicName: task.epicName || "",
                startDate: startDateVal,
                endDate: endDateVal,
                status: task.status || "",
                priority: task.priority || "",
                estimate: estimateVal,
                assignee: task.assignee || "",
                labels: Array.isArray(task.labels) ? task.labels.join(', ') : (task.labels || ""),
                sprint: task.sprint || "",
                parentTask: task.parentTaskId || "",
            });

            // Also update setValue for the calendar components
            setValue("startDate", startDateVal);
            setValue("endDate", endDateVal);
            setValue("type", task.type || "");
            setValue("parentTask", task.parentTaskId || "");
        }
    }, [task, reset, setValue]);

    // Auto-calculate end date when start date or estimate changes
    useEffect(() => {
        if (startDate && estimateDays && estimateDays > 0) {
            calculateEndDate(startDate, parseInt(estimateDays));
        }
    }, [startDate, estimateDays, calculateEndDate]);

    // Sync state changes with form values
    useEffect(() => {
        if (startDate) {
            setValue("startDate", startDate, { shouldValidate: true });
        }
    }, [startDate, setValue]);

    useEffect(() => {
        if (endDateValue) {
            setValue("endDate", endDateValue, { shouldValidate: true });
        }
    }, [endDateValue, setValue]);

    useEffect(() => {
        if (estimateDays) {
            setValue("estimate", estimateDays, { shouldValidate: true });
        }
    }, [estimateDays, setValue]);

    // Handle start date change
    const handleStartDateChange = (e) => {
        const newStartDate = e.target.value;
        setStartDate(newStartDate);
        setValue("startDate", newStartDate, { shouldValidate: true });
    };

    // Handle estimate days change
    const handleEstimateChange = (e) => {
        const newEstimate = e.target.value;
        setEstimateDays(newEstimate);
        setValue("estimate", newEstimate, { shouldValidate: true });
    };

    // Handle end date change and validation
    const handleEndDateChange = async (e) => {
        const newEndDate = e.target.value;
        setEndDateValue(newEndDate);
        setValue("endDate", newEndDate, { shouldValidate: true });

        // Validate the selected end date
        if (startDate && estimateDays && newEndDate) {
            await validateEndDate(startDate, parseInt(estimateDays), newEndDate);
        }
    };

    // Handles form submission
    const onSubmit = async (data) => {
        // Log the submitted form data for debugging
        console.log("Form submitted with data:", data);
        console.log("Current state - startDate:", startDate, "estimateDays:", estimateDays, "endDateValue:", endDateValue);

        // Check if start date is provided, alert if missing
        if (!data.startDate) {
            console.error("Start date validation failed - data.startDate:", data.startDate);
            alert("Start date is required");
            return;
        }

        // Prepare the payload object to match backend API expectations
        const payload = {
            type: data.type, // Task type (task, epic, etc.)
            title: data.title, // Task title
            description: data.description || null, // Task description or null
            startDate: data.startDate, // Start date
            dueDate: data.endDate || null, // End date or null
            status: data.status, // Task status
            priority: data.priority, // Task priority
            // Convert estimate to PostgreSQL INTERVAL format if provided (days instead of hours)
            originalEstimate: data.estimate ? `P${data.estimate}D` : null,
            // These fields are set to null for now (require UUIDs)
            assigneeId: null,
            epicId: null,
            sprintId: null,
            // Set parent task ID if provided
            parentTaskId: data.parentTask || parentId || null,
            // Split labels by comma, trim whitespace, filter out empty strings
            labels: data.labels ? data.labels.split(',').map(l => l.trim()).filter(l => l.length > 0) : null
        };

        // Log the payload for debugging
        console.log("Sending payload:", payload);

        try {
            if (task) {
                // If editing, update the existing task
                console.log("Updating task:", task.taskId || task.id);
                const response = await fetch(`/tasks/${task.taskId || task.id}`, {
                    method: "PUT",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(payload)
                });
                // If response is not OK, throw error with response text
                if (!response.ok) {
                    const errorText = await response.text();
                    throw new Error(`Failed to update task: ${errorText}`);
                }
                // Log success
                console.log("Task updated successfully");
            } else {
                // If creating, send POST request to create new task
                console.log("Creating new task");
                const response = await fetch("/tasks", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(payload)
                });
                // If response is not OK, throw error with response text
                if (!response.ok) {
                    const errorText = await response.text();
                    throw new Error(`Failed to create task: ${errorText}`);
                }
                // Parse and log the created task result
                const result = await response.json();
                console.log("Task created successfully:", result);
            }
            // Navigate to the view-tasks page after success
            navigate("/view-tasks");
        } catch (error) {
            // Log and alert any errors that occur during save
            console.error("Error saving task:", error);
            alert(`Failed to save task: ${error.message}`);
        }
    };

    return (
        <div className="form">
            <div className="form-header">
                <h1>{task ? "Edit Task" : "+Create Task"}</h1>
            </div>
            <div className="form-container">
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-subcontainer">
                        <div className="type-name">

                            {/*type*/}
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Type</label>

                            </div>
                            <select
                                {...register("type", { required: true })}
                                className="box"
                                value={selectedTaskType}
                                onChange={handleTaskTypeChange}
                            >
                                <option value="">Select type</option>
                                <option value="epic">Epic</option>
                                <option value="story">Story</option>
                                <option value="task">Task</option>
                                <option value="sub_task">Sub Task</option>
                            </select>
                            {errors.type && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>




                        <div className="type-name">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Task Name/Title</label>

                            </div>
                            <input
                                {...register("title", { required: true })}
                                className="box"
                            />
                            {errors.title && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>


                    </div>

                    <div className="form-subcontainer">

                        {/* Task Description */}
                        <div className="description">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Description</label>

                            </div>
                            <textarea
                                {...register("description")}
                                rows={3}
                                className="box"
                            />
                        </div>

                    </div>

                    <div className="form-subcontainer">
                        {/* Linked Epic Name */}
                        <div className="epic-name">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Linked Epic Name</label>

                            </div>
                            <input
                                {...register("epicName")}
                                className="box"
                            />
                        </div>



                    </div>

                    <div className="form-subcontainer">

                            <div className="date">
                                <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Start Date</label>
                                </div>
                                <HolidayCalendar
                                    value={startDate}
                                    onChange={(date) => {
                                        setStartDate(date);
                                        // Update the form field using setValue - this ensures form validation works
                                        setValue("startDate", date, { shouldValidate: true, shouldDirty: true });
                                    }}
                                    holidays={holidays}
                                    shouldDisableDate={shouldDisableDate}
                                    getDateClassName={getDateClassName}
                                    getHolidayInfo={getHolidayInfo}
                                    placeholder="Select start date"
                                    required
                                    className="box"
                                />
                                {/* Hidden input for form validation */}
                                <input
                                    type="hidden"
                                    {...register("startDate", { 
                                        required: "Start date is required"
                                    })}
                                    value={startDate || ''}
                                />
                                {errors.startDate && <span className="text-red-500 text-sm">{errors.startDate.message}</span>}
                            </div>

                            <div className="date">
                                <div className="title">
                                <label className="block text-sm font-medium text-gray-700">End Date/Due Date</label>
                                </div>
                                <HolidayCalendar
                                    value={endDateValue}
                                    onChange={(date) => {
                                        setEndDateValue(date);
                                        // Update the form field using setValue with shouldDirty
                                        setValue("endDate", date, { shouldValidate: true, shouldDirty: true });
                                        // Validate the selected end date
                                        if (startDate && estimateDays && date) {
                                            validateEndDate(startDate, parseInt(estimateDays), date);
                                        }
                                    }}
                                    holidays={holidays}
                                    minDate={calculatedEndDate} // Restrict to calculated date or later
                                    shouldDisableDate={(date) => {
                                        // Disable if before calculated end date
                                        if (calculatedEndDate && new Date(date) < new Date(calculatedEndDate)) {
                                            return true;
                                        }
                                        return shouldDisableDate ? shouldDisableDate(date) : false;
                                    }}
                                    getDateClassName={getDateClassName}
                                    getHolidayInfo={getHolidayInfo}
                                    placeholder={calculatedEndDate ? `Suggested: ${calculatedEndDate}` : "Select end date"}
                                    className="box"
                                    style={{
                                        backgroundColor: calculatedEndDate && !endDateValue ? '#f3f4f6' : 'white',
                                        color: calculatedEndDate && !endDateValue ? '#6b7280' : 'black'
                                    }}
                                />
                                {/* Hidden input for form validation */}
                                <input
                                    type="hidden"
                                    {...register("endDate")}
                                    value={endDateValue || ''}
                                />
                                {calculatedEndDate && (
                                    <div className="text-sm text-gray-600 mt-1">
                                        {isCalculating ? 'Calculating...' : `Suggested end date: ${calculatedEndDate}`}
                                    </div>
                                )}
                                {validationMessage && (
                                    <div className="text-sm text-red-500 mt-1">{validationMessage}</div>
                                )}
                            </div>


                    </div>

                    <div className="form-subcontainer">
                        {/* Status */}
                        <div className="status-priority">
                            <div className="title">

                            <label className="block text-sm font-medium text-gray-700">Status</label>
                            </div>
                            <select
                                {...register("status", { required: true })}
                                className="box"
                            >
                                <option value="">Select status</option>
                                <option value="To Do">To Do</option>
                                <option value="In Progress">In Progress</option>
                                <option value="Done">Done</option>
                            </select>
                            {errors.status && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>

                        {/* Priority */}
                        <div className="status-priority">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Priority</label>

                            </div>
                            <select
                                {...register("priority")}
                                className="box"
                            >
                                <option value="">Select priority</option>
                                <option value="High">High</option>
                                <option value="Medium">Medium</option>
                                <option value="Low">Low</option>
                            </select>
                        </div>

                    </div>

                    <div className="form-subcontainer">
                        <div className="time-assignee">
                            <div className="title">

                            <label className="block text-sm font-medium text-gray-700">Original Estimate (days)</label>
                            </div>
                            <input
                                type="number"
                                step="0.5"
                                min="0"
                                {...register("estimate", { 
                                    onChange: handleEstimateChange,
                                    min: { value: 0, message: "Estimate must be positive" }
                                })}
                                className="box"
                                placeholder="Enter days (e.g., 1.5)"
                            />
                            {errors.estimate && <span className="text-red-500 text-sm">{errors.estimate.message}</span>}
                        </div>
                        {/* Assignee */}
                        <div className="time-assignee">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Assignee</label>

                            </div>
                            <input
                                {...register("assignee")}
                                className="box"
                            />
                        </div>

                    </div>

                    <div className="form-subcontainer">
                        {/* Labels/Tags */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Labels</label>

                            </div>
                            <input
                                {...register("labels")}
                                className="box"
                                placeholder="tag1, tag2, tag3"
                            />
                        </div>

                        {/* Sprint */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Sprint</label>

                            </div>
                            <input
                                {...register("sprint")}
                                className="box"
                            />
                        </div>

                        {/* Parent Task */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Parent Task</label>
                            </div>

                            {/* Show info about parent if set from URL */}
                            {parentTask && (
                                <div className="parent-task-info">
                                    <small>Creating subtask under: <strong>{parentTask.title}</strong> ({parentTask.type})</small>
                                </div>
                            )}

                            <select
                                {...register("parentTask")}
                                className="box"
                                disabled={selectedTaskType === 'epic' || (parentId && parentTask)}
                            >
                                <option value="">
                                    {selectedTaskType === 'epic' ? 'Epics cannot have parents' : 'Select parent task'}
                                </option>
                                {selectedTaskType !== 'epic' && validParentTasks.map((t) => (
                                    <option key={t.taskId || t.id} value={t.taskId || t.id}>
                                        {t.title} ({t.type})
                                    </option>
                                ))}
                            </select>

                            {selectedTaskType !== 'epic' && validParentTasks.length === 0 && selectedTaskType && (
                                <small className="text-gray-500">
                                    No valid parent tasks available for {selectedTaskType}
                                </small>
                            )}
                        </div>

                    </div>

                    <div className="form-subcontainer">
                        <button
                            type="submit"
                            className="button"
                        >
                            {task ? "Update Task" : "Create Task"}
                        </button>
                    </div>

                </form>


            </div>
        </div>
    );
};

export default TaskForm;
