import { useLocation, useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { useEffect, useState } from "react";
import "./TaskForm.css"

const TaskForm = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const task = location.state?.task; // get task if passed

    const [allTasks, setAllTasks] = useState([]);

    // Fetch all tasks for parent task dropdown (API integration spot)
    useEffect(() => {
        // Fetch all tasks for parent task dropdown from backend
        const fetchTasks = async () => {
            try {
                const response = await fetch("/tasks");
                if (!response.ok) throw new Error("Failed to fetch tasks");
                const tasks = await response.json();
                setAllTasks(tasks);
            } catch (error) {
                setAllTasks([]);
            }
        };
        fetchTasks();
    }, []);

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors },
    } = useForm();

    // Prefill form if editing
    useEffect(() => {
        if (task) {
            reset({
                type: task.type ? task.type.toLowerCase() : "",
                title: task.title || "",
                description: task.task_description || "",
                epicName: task.epicName || "",
                startDate: task.start_date || "",
                endDate: task.due_date || "",
                status: task.status || "",
                priority: task.priority || "",
                estimate: task.estimation || "",
                assignee: task.assignee || "",
                labels: task.labels || "",
                sprint: task.sprint || "",
                parentTask: task.parentTask || "",
            });
        }
    }, [task, reset]);

    const onSubmit = async (data) => {
        // Validate required fields
        if (!data.startDate) {
            alert("Start date is required");
            return;
        }

        // Prepare the payload to match backend expectations
        const payload = {
            type: data.type,
            title: data.title,
            description: data.description || null,
            startDate: data.startDate,
            dueDate: data.endDate || null,
            status: data.status,
            priority: data.priority,
            // Convert estimate to Duration format if needed (PostgreSQL INTERVAL format)
            originalEstimate: data.estimate ? `PT${data.estimate}H` : null,
            // For now, we'll leave these as null since they need UUIDs
            assigneeId: null,
            epicId: null,
            sprintId: null,
            parentTaskId: null,
            labels: data.labels ? data.labels.split(',').map(l => l.trim()).filter(l => l.length > 0) : null
        };

        try {
            if (task) {
                // Update existing task
                const response = await fetch(`/tasks/${task.taskId || task.id}`, {
                    method: "PUT",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(payload)
                });
                if (!response.ok) {
                    const errorText = await response.text();
                    throw new Error(`Failed to update task: ${errorText}`);
                }
            } else {
                // Create new task
                const response = await fetch("/tasks", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(payload)
                });
                if (!response.ok) {
                    const errorText = await response.text();
                    throw new Error(`Failed to create task: ${errorText}`);
                }
            }
            navigate("/view-tasks");
        } catch (error) {
            console.error("Error saving task:", error);
            alert(`Failed to save task: ${error.message}`);
        }
    };

    return (
        <div className="form">
            <div className="form-header">
                <h1>{task ? "Edit Task" : "+Create Task"}</h1>
            </div>
            <div className="form-container">
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-subcontainer">
                        <div className="type-name">

                            {/*type*/}
                            <div className="title">
                                <label className="block text-sm font-medium text-gray-700">Type</label>

                            </div>
                            <select
                                {...register("type", { required: true })}
                                className="box"
                            >
                                <option value="">Select type</option>
                                <option value="task">Task</option>
                                <option value="epic">Epic</option>
                                <option value="story">Story</option>
                                <option value="sub_task">Sub Task</option>
                            </select>
                            {errors.type && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>




                        <div className="type-name">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Task Name/Title</label>

                            </div>
                            <input
                                {...register("title", { required: true })}
                                className="box"
                            />
                            {errors.title && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>


                    </div>

                    <div className="form-subcontainer">

                        {/* Task Description */}
                        <div className="description">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Description</label>

                            </div>
                            <textarea
                                {...register("description")}
                                rows={3}
                                className="box"
                            />
                        </div>

                    </div>

                    <div className="form-subcontainer">
                        {/* Linked Epic Name */}
                        <div className="epic-name">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Linked Epic Name</label>

                            </div>
                            <input
                                {...register("epicName")}
                                className="box"
                            />
                        </div>



                    </div>

                    <div className="form-subcontainer">

                            <div className="date">
                                <div className="title">

                                <label className="block text-sm font-medium text-gray-700">Start Date</label>
                                </div>
                                <input
                                    type="date"
                                    {...register("startDate")}
                                    className="box"
                                />
                            </div>

                            <div className="date">
                                <div className="title">

                                <label className="block text-sm font-medium text-gray-700">End Date/Due Date</label>
                                </div>
                                <input
                                    type="date"
                                    {...register("endDate")}
                                    className="box"
                                />
                            </div>


                    </div>

                    <div className="form-subcontainer">
                        {/* Status */}
                        <div className="status-priority">
                            <div className="title">

                            <label className="block text-sm font-medium text-gray-700">Status</label>
                            </div>
                            <select
                                {...register("status", { required: true })}
                                className="box"
                            >
                                <option value="">Select status</option>
                                <option value="To Do">To Do</option>
                                <option value="In Progress">In Progress</option>
                                <option value="Done">Done</option>
                            </select>
                            {errors.status && <span className="text-red-500 text-sm">This field is required</span>}
                        </div>

                        {/* Priority */}
                        <div className="status-priority">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Priority</label>

                            </div>
                            <select
                                {...register("priority")}
                                className="box"
                            >
                                <option value="">Select priority</option>
                                <option value="High">High</option>
                                <option value="Medium">Medium</option>
                                <option value="Low">Low</option>
                            </select>
                        </div>

                    </div>

                    <div className="form-subcontainer">
                        <div className="time-assignee">
                            <div className="title">

                            <label className="block text-sm font-medium text-gray-700">Original Estimate (hours)</label>
                            </div>
                            <input
                                type="number"
                                {...register("estimate")}
                                className="box"
                            />
                        </div>
                        {/* Assignee */}
                        <div className="time-assignee">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Assignee</label>

                            </div>
                            <input
                                {...register("assignee")}
                                className="box"
                            />
                        </div>

                    </div>

                    <div className="form-subcontainer">
                        {/* Labels/Tags */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Labels</label>

                            </div>
                            <input
                                {...register("labels")}
                                className="box"
                                placeholder="tag1, tag2, tag3"
                            />
                        </div>

                        {/* Sprint */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Sprint</label>

                            </div>
                            <input
                                {...register("sprint")}
                                className="box"
                            />
                        </div>

                        {/* Parent Task */}
                        <div className="labels-sprint-parent">
                            <div className="title">
                            <label className="block text-sm font-medium text-gray-700">Parent Task</label>

                            </div>
                            <select
                                {...register("parentTask")}
                                className="box"
                            >
                                <option value="">Select parent task</option>
                                {allTasks.map((t) => (
                                    <option  value={t.title}>
                                        {t.title}
                                    </option>
                                ))}
                            </select>
                        </div>

                    </div>

                    <div className="form-subcontainer">
                        <button
                            type="submit"
                            className="button"
                        >
                            {task ? "Update Task" : "Create Task"}
                        </button>
                    </div>

                </form>


            </div>
        </div>
    );
};

export default TaskForm;
