// Import React and state/effect hooks
import React, { useEffect, useState } from "react";
// Import navigation from React Router
import { useNavigate } from "react-router-dom";
// Import the API helpers for task operations
import { getAllTasks, deleteTask } from "../api/taskApi";

const TaskView = () => {
  // State to hold the list of tasks
  const [tasks, setTasks] = useState([]);
  // State to control the confirm delete modal visibility
  const [showConfirm, setShowConfirm] = useState(false);
  // State to remember which task is being considered for deletion
  const [taskToDelete, setTaskToDelete] = useState(null);

  // Get navigation function from React Router
  const navigate = useNavigate();

  // Fetch all tasks from backend when component mounts
  useEffect(() => {
    // Call the API helper to fetch all tasks
    getAllTasks().then(setTasks);
  }, []);

  // Handle clicking the "Add Task" button
  const handleAddTask = () => {
    // Navigate to the add-task page (TaskForm)
    navigate("/add-task");
  };

  // Handle clicking the "Delete" button for a specific task
  const handleDeleteClick = (task) => {
    // Set which task is being considered for deletion
    setTaskToDelete(task);
    // Show the confirmation modal
    setShowConfirm(true);
  };

  // Confirm deletion after user agrees in modal
  const handleConfirmDelete = async () => {
    // Call backend API to delete the task
    const success = await deleteTask(taskToDelete.id);
    if (success) {
      // If successful, remove the task from the list in state
      setTasks(tasks.filter((t) => t.id !== taskToDelete.id));
    }
    // Hide the modal and clear the task to delete
    setShowConfirm(false);
    setTaskToDelete(null);
  };

  // Cancel the delete operation
  const handleCancelDelete = () => {
    // Simply hide the confirmation modal and reset the task to delete
    setShowConfirm(false);
    setTaskToDelete(null);
  };

  return (
      <div className="task-view">
        {/* Task Table */}
        <div className="table-scroll-container">
          <table className="task-list-table">
            <thead>
            <tr>
              <th>Title</th>
              <th>Status</th>
              <th>Assignee</th>
              <th>Due date</th>
              <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {/* Render each task as a row */}
            {tasks.map((task) => (
                <tr key={task.id}>
                  <td>{task.title}</td>
                  <td>{task.status}</td>
                  <td>{task.assignee}</td>
                  <td>{task.due_date || task.endDate}</td>
                  <td>
                    {/* Button to edit (navigate to TaskForm with state) */}
                    <button
                        onClick={() => navigate("/add-task", { state: { task } })}
                    >
                      Edit
                    </button>
                    {/* Button to delete (show confirm modal) */}
                    <button onClick={() => handleDeleteClick(task)}>Delete</button>
                  </td>
                </tr>
            ))}
            </tbody>
          </table>
        </div>
        {/* Button to add a new task */}
        <div className="create-row">
          <button className="create-btn" onClick={handleAddTask}>
            + Create
          </button>
        </div>
        {/* Confirmation modal for deletion */}
        {showConfirm && (
            <div className="custom-confirm-overlay">
              <div className="custom-confirm-box">
                <p>Are you sure you want to delete this task?</p>
                <div className="confirm-buttons">
                  <button className="button" onClick={handleConfirmDelete}>
                    OK
                  </button>
                  <button className="button" onClick={handleCancelDelete}>
                    Cancel
                  </button>
                </div>
              </div>
            </div>
        )}
      </div>
  );
};

export default TaskView;
