import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import HierarchicalTaskTree from "../components/HierarchicalTaskTree";
import TaskCard from "../components/TaskList";
import "./TaskView.css";
import Filter from "../components/Filter";

const TaskView = () => {
  const [tasks, setTasks] = useState([]);
  const [showConfirm, setShowConfirm] = useState(false);
  const [taskToDelete, setTaskToDelete] = useState(null);
  const [filter, setFilter] = useState("");
  const [activeFilters, setActiveFilters] = useState(null);
  const [expandedTasks, setExpandedTasks] = useState(new Set());
  const [loading, setLoading] = useState(false);
  const [viewMode, setViewMode] = useState('hierarchy'); // 'hierarchy' or 'table'
  const navigate = useNavigate();

  useEffect(() => {
    fetchTasks();
  }, []);

  // Fetch tasks from backend API
  const fetchTasks = async (filterParams = null) => {
    setLoading(true);
    try {
      let url = "/tasks";

      // For hierarchy view, use the hierarchy endpoint
      if (viewMode === 'hierarchy' && !filterParams) {
        url = "/tasks/hierarchy";
      } else if (filterParams) {
        // If filters are provided, use the filter endpoint
        const queryParams = new URLSearchParams();

        if (filterParams.startDate) queryParams.append('startDate', filterParams.startDate);
        if (filterParams.endDate) queryParams.append('endDate', filterParams.endDate);
        if (filterParams.types && filterParams.types.length > 0) {
          queryParams.append('types', filterParams.types.join(','));
        }
        if (filterParams.assignee) queryParams.append('assignee', filterParams.assignee);
        if (filterParams.status) queryParams.append('status', filterParams.status);
        if (filterParams.priority) queryParams.append('priority', filterParams.priority);

        url = `/tasks/filter?${queryParams.toString()}`;
      }

      console.log("Fetching tasks from", url);
      const response = await fetch(url);
      if (!response.ok) throw new Error("Failed to fetch tasks");
      const data = await response.json();
      console.log("Fetched tasks:", data);

      // For hierarchy endpoint, keep the hierarchical structure
      if (viewMode === 'hierarchy' && !filterParams) {
        setTasks(data); // Keep the hierarchical structure
        setHierarchyData(data); // Store hierarchy data separately
      } else {
        setTasks(data);
        setHierarchyData([]); // Clear hierarchy data for table view
      }
    } catch (error) {
      console.error("Error fetching tasks:", error);
      setTasks([]);
    } finally {
      setLoading(false);
    }
  };

  // Handle filter application
  const handleFilterApply = (filterData) => {
    console.log("Applying filters:", filterData);

    // Process the filter data
    const processedFilters = {
      startDate: filterData.startDate || null,
      endDate: filterData.endDate || null,
      types: [],
      assignee: filterData.assignee || null,
      status: filterData.status || null,
      priority: filterData.priority || null
    };

    // Process work types from checkboxes
    if (filterData.epic) processedFilters.types.push('epic');
    if (filterData.story) processedFilters.types.push('story');
    if (filterData.subtask) processedFilters.types.push('sub_task');
    if (filterData.task) processedFilters.types.push('task');

    setActiveFilters(processedFilters);
    fetchTasks(processedFilters);
  };

  // Handle filter clear
  const handleFilterClear = () => {
    console.log("Clearing filters");
    setActiveFilters(null);
    fetchTasks(); // Fetch all tasks without filters
  };

  const handleAddTask = () => {
    navigate("/add-task");
  };

  const handleDeleteClick = (task) => {
    setTaskToDelete(task);
    setShowConfirm(true);
  };

  const handleConfirmDelete = async () => {
    try {
      console.log("Deleting task:", taskToDelete);
      // Use the correct task ID field based on the Task entity
      const taskId = taskToDelete.taskId || taskToDelete.id;
      console.log("Using task ID:", taskId);

      const response = await fetch(`/tasks/${taskId}`, {
        method: "DELETE"
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to delete task: ${errorText}`);
      }

      console.log("Task deleted successfully");
      // Remove from local state after successful backend deletion
      setTasks(tasks.filter(t => (t.taskId || t.id) !== taskId));
      setShowConfirm(false);
      setTaskToDelete(null);

      // Refresh the task list to ensure consistency
      fetchTasks(activeFilters);
    } catch (error) {
      console.error("Error deleting task:", error);
      alert(`Failed to delete task: ${error.message}`);
      setShowConfirm(false);
      setTaskToDelete(null);
    }
  };

  const handleCancelDelete = () => {
    setShowConfirm(false);
    setTaskToDelete(null);
  };

  // Handle task editing
  const handleEditTask = (task) => {
    navigate(`/edit-task/${task.id || task.taskId}`);
  };

  // Handle creating a subtask
  const handleCreateSubtask = (parentTaskId) => {
    navigate(`/add-task?parentId=${parentTaskId}`);
  };

  // Handle expand/collapse of task tree nodes
  const handleToggleExpand = (taskId, forceState = null) => {
    setExpandedTasks(prev => {
      const newExpanded = new Set(prev);
      if (forceState === true) {
        newExpanded.add(taskId);
      } else if (forceState === false) {
        newExpanded.delete(taskId);
      } else {
        if (newExpanded.has(taskId)) {
          newExpanded.delete(taskId);
        } else {
          newExpanded.add(taskId);
        }
      }
      return newExpanded;
    });
  };

  // Handle view mode toggle
  const handleViewModeToggle = () => {
    const newMode = viewMode === 'hierarchy' ? 'table' : 'hierarchy';
    setViewMode(newMode);
    // Refetch tasks for the new view mode
    fetchTasks(activeFilters);
  };

  return (
    <div className="modern-task-view">
      <div className="task-view-header">
        <h2>Task Management</h2>
        <div className="header-actions">
          <div className="view-toggle">
            <button 
              className={`view-toggle-btn ${viewMode === 'hierarchy' ? 'active' : ''}`}
              onClick={handleViewModeToggle}
            >
              {viewMode === 'hierarchy' ? 'ðŸ“‹ Hierarchy' : 'ðŸ“Š Table'}
            </button>
          </div>
          <button className="modern-create-btn" onClick={handleAddTask}>
            <span className="plus-icon">+</span> Create Task
          </button>
        </div>
      </div>

      <div className="modern-filter-section">
        <Filter onFilterApply={handleFilterApply} onFilterClear={handleFilterClear} activeFilters={activeFilters} />
      </div>

      <div className="task-content-container">
        {viewMode === 'hierarchy' ? (
          <HierarchicalTaskTree
            tasks={tasks}
            onEdit={handleEditTask}
            onDelete={(taskId) => handleDeleteClick(tasks.find(t => (t.id || t.taskId) === taskId))}
            onCreateSubtask={handleCreateSubtask}
            expandedTasks={expandedTasks}
            onToggleExpand={handleToggleExpand}
          />
        ) : (
          <div className="modern-table-container">
            <div className="table-scroll-container">
              <table className="modern-task-table">
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Description</th>
                    <th>Assignee</th>
                    <th>Type</th>
                    <th>Status</th>
                    <th>Priority</th>
                    <th>Estimation (Days)</th>
                    <th>Start Date</th>
                    <th>Due Date</th>
                    <th>Labels</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {tasks
                    .filter(task =>
                      (task.title?.toLowerCase().includes(filter.toLowerCase()) ||
                      task.assignee?.toLowerCase().includes(filter.toLowerCase()) ||
                      task.status?.toLowerCase().includes(filter.toLowerCase()))
                    )
                    .map((task, idx) => (
                      <TaskCard
                        key={task.task_id || idx}
                        task={task}
                        onDelete={() => handleDeleteClick(task)}
                      />
                    ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>

      {showConfirm && (
        <div className="modern-confirm-overlay">
          <div className="modern-confirm-box">
            <h3>Confirm Deletion</h3>
            <p>Are you sure you want to delete this task?</p>
            <div className="confirm-buttons">
              <button className="confirm-btn delete" onClick={handleConfirmDelete}>Delete</button>
              <button className="confirm-btn cancel" onClick={handleCancelDelete}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TaskView;
